var source = "";
var text = absol.base64.base64DecodeUnicode(source);
var lines = text.trim().split('\n');
var rawTableData = lines.map(t => t.split("\t"));
var adapter = {
    data: {
        rowsPerPage: Infinity,
        head: {
            rows: [
                {
                    cells: [
                        { child: { text: '' } },
                        { child: { text: 'Tên cột' } },
                        { child: { text: 'Mã' } },
                        { child: { text: 'Thiết lập' } },
                        { child: { text: 'Chiều rộng cột' } },
                        { child: { text: 'Loại' } },
                        { child: { text: '' } },
                    ]
                }
            ]
        },
        body: {
            rows: rawTableData.concat(rawTableData).concat(rawTableData).concat(rawTableData).map((rawRow, i) => {
                return {
                    cells: [
                        {
                            class: (i < 5 || i > 15) ? 'as-no-drag' : 'as-drag-zone',
                            child: (i < 5 || i > 15) ? {} : 'span.mdi.mdi-drag'
                        },
                        {
                            child: { tag: 'span', child: { text: rawRow[0] } }
                        },
                        {
                            child: { tag: 'span', child: { text: rawRow[1] } }
                        },
                        {
                            child: { tag: 'span', child: { text: rawRow[2] } }
                        },
                        {
                            child: { tag: 'span', child: { text: rawRow[3] } }
                        },
                        {
                            child: { tag: 'span', child: { text: rawRow[4] } }
                        },
                        {
                            child: {
                                tag: 'i',
                                class: 'material-icons',
                                child: { text: rawRow[5] }
                            }
                        }
                    ]
                }

            })
        }
    }
}


var table = absol._({
    tag: 'dynamictable',
    props: {
        adapter: adapter
    },
    on: {
        orderchange: function (event) {
            console.log(event)
        }
    }
})


var singlePage = absol._({
    tag: 'singlepage',
    child: [
        table
    ]
}).addTo(document.body);
